import React from "react";
import ProfileImageWithDefault from "./ProfileImageWithDefault";

const PostView = (props) => {
  const { post } = props;
  const { user, content } = post;
  const { username, displayName, image } = user;

  return (
    <div className="card p-1">
      <div className="d-flex align-items-center">
        <ProfileImageWithDefault
          image={image}
          width="32"
          height="32"
          className="rounded-circle m-1"
        />
        <div className="flex-grow-1">
          <h6 className="mb-0">
            <span className="font-weight-bold">{displayName}</span>
            <span className="text-muted ml-2">@{username}</span>
          </h6>
        </div>
      </div>

      <div className="px-3 py-2">{content}</div>
      <div className="d-flex justify-content-end text-muted">
        <button className="btn btn-link btn-sm">Like</button>
        <button className="btn btn-link btn-sm">Retweet</button>
      </div>
    </div>
  );
};

export default PostView;
