import React from "react";
import ProfileImageWithDefault from "./ProfileImageWithDefault";
import { Link } from "react-router-dom";
import { format, register } from "timeago.js"; // `register` ekledik
import { useTranslation } from "react-i18next";
import en from 'timeago.js/lib/lang/en'; // İngilizce dil dosyası

// Dil dosyasını kaydediyoruz
register("en", en);

const PostView = (props) => {
  const { post } = props;
  const { user, content, timestamp } = post;
  const { username, displayName, image } = user;

  const { i18n } = useTranslation();

  const formatted = format(timestamp, 'en'); // 'en' İngilizce dilini temsil eder
  return (
    <div className="card p-1">
      <div className="d-flex">
        <ProfileImageWithDefault
          image={image}
          width="32"
          height="32"
          className="rounded-circle m-1"
        />
        <div className="flex-fill m-auto px-2">
          <Link
            to={`/user/${username}`}
            className="text-dark"
            style={{ textDecoration: "none" }}
          >
            <h6 className="d-inline">
              <span>{displayName}</span>
              <span className="text-muted"> @{username}</span>
            </h6>
            <span> - </span>
            <span>{formatted}</span>
          </Link>
        </div>
      </div>
      <div className="px-5"> {content} </div>
    </div>
  );
};

export default PostView;
