import React from "react";
import { Link } from "react-router-dom";
import ProfileImageWithDefault from "./ProfileImageWithDefault";
import VerifiedBadge from "./VerifiedBadge";
import FollowButton from "./FollowButton"; // FollowButton bileşenini ekledik

const UserListItem = (props) => {
  const { user, loggedInUsername } = props; // loggedInUsername prop'unu alıyoruz
  const { username, displayName, image, verified, admin, following } = user;

  return (
    <div className="list-group-item">
      <div className="flex-grow-1 d-flex flex-column justify-content-center ml-2">
        <div className="d-flex">
          <ProfileImageWithDefault
            className="rounded-circle"
            width="32"
            height="32"
            alt={`${username} profile`}
            image={image}
          />

          <Link to={`/users/${username}`} className="text-dark">
            <span className="font-weight-bold">{displayName}</span>
          </Link>
          <span className="text-muted">@{username}</span>
        </div>

        <div>
          <FollowButton
            loggedInUsername={loggedInUsername}
            username={username}
            isFollowing={following}
          />
        </div>
      </div>
    </div>
  );
};

export default UserListItem;
